// Analizar datos qu√≠micos para detectar anomal√≠as espec√≠ficas del laboratorio
const data = $input.first().json;

const alerts = [];
const warnings = [];

// Verificar rendimiento qu√≠mico bajo (basado en estructura CSV)
if (data.avg_rendimiento < 75) {
  alerts.push({
    type: 'rendimiento_quimico',
    severity: data.avg_rendimiento < 60 ? 'high' : 'medium',
    message: `Rendimiento qu√≠mico promedio: ${data.avg_rendimiento.toFixed(1)}%`,
    threshold: '75%',
    current_value: `${data.avg_rendimiento.toFixed(1)}%`,
    timestamp: new Date().toISOString()
  });
}

// Verificar equipos qu√≠micos con problemas
const equipos_quimicos = ['phmetro', 'espectrofotometro', 'centrifuga', 'analizador hematologico'];
if (data.equipment_status) {
  equipos_quimicos.forEach(equipo => {
    const status = data.equipment_status[equipo];
    if (status && status.status !== 'ok') {
      alerts.push({
        type: 'equipo_quimico',
        severity: 'high',
        message: `Problema en ${equipo}: ${status.comentario || 'Estado no OK'}`,
        equipment: equipo,
        timestamp: new Date().toISOString()
      });
    }
  });
}

// Verificar muestras procesadas muy bajas
if (data.total_muestras_procesadas < 50) {
  warnings.push({
    type: 'baja_produccion',
    severity: 'medium',
    message: `Pocas muestras procesadas: ${data.total_muestras_procesadas}`,
    expected: '> 50 muestras/d√≠a',
    timestamp: new Date().toISOString()
  });
}

// Verificar alta tasa de repeticiones
if (data.tasa_repeticiones > 0.2) {
  alerts.push({
    type: 'alta_repeticion',
    severity: data.tasa_repeticiones > 0.3 ? 'high' : 'medium',
    message: `Alta tasa de repeticiones: ${(data.tasa_repeticiones * 100).toFixed(1)}%`,
    threshold: '20%',
    current_value: `${(data.tasa_repeticiones * 100).toFixed(1)}%`,
    timestamp: new Date().toISOString()
  });
}

// Verificar distribuci√≥n de turnos desbalanceada
if (data.turnos_distribution) {
  const turnos = Object.keys(data.turnos_distribution);
  const valores = Object.values(data.turnos_distribution);
  const max_val = Math.max(...valores);
  const min_val = Math.min(...valores);

  if (max_val > min_val * 3) {
    warnings.push({
      type: 'desbalance_turnos',
      severity: 'low',
      message: 'Distribuci√≥n desbalanceada entre turnos',
      details: data.turnos_distribution,
      timestamp: new Date().toISOString()
    });
  }
}

return {
  alerts: alerts,
  warnings: warnings,
  total_alerts: alerts.length,
  total_warnings: warnings.length,
  check_timestamp: new Date().toISOString(),
  monitoring_frequency: '5_seconds',
  chemical_data_summary: data
};

// Preparar notificaci√≥n de alerta cr√≠tica
const alertData = $input.first().json;

const criticalAlerts = alertData.alerts.filter(alert => alert.severity === 'high');
const mediumAlerts = alertData.alerts.filter(alert => alert.severity === 'medium');

const notification = {
  type: 'lab_alert',
  timestamp: new Date().toISOString(),
  summary: {
    critical_count: criticalAlerts.length,
    medium_count: mediumAlerts.length,
    warning_count: alertData.total_warnings
  },
  critical_alerts: criticalAlerts,
  medium_alerts: mediumAlerts,
  warnings: alertData.warnings,
  requires_immediate_attention: criticalAlerts.length > 0
};

return notification;

// Preparar email de alerta cr√≠tica
