const emailData = {
  to: process.env.LAB_MANAGER_EMAIL || 'manager@lab.com',
  cc: process.env.LAB_SUPERVISOR_EMAIL || 'supervisor@lab.com',
  subject: 'üö® ALERTA CR√çTICA - Laboratorio Qu√≠mico',
  html: `
